MAN.EXAMPLE:
ap=circle(12);
act_dim=16;act_pitch=1;act_vel=1e-4;
act=actuator_array(act_dim,act_dim,act_pitch,act_vel);
dm=ideal_dm(ap,act);dm=set_dm_timestamp(dm,0);

sgn=-1;
actuator_command_meters=0.5e-6;pos=[];
for i=1:act_dim
	sgn=-1*sgn;
	for j=1:act_dim
		sgn=-1*sgn;
		pos=[pos,sgn*actuator_command_meters];
	end
end  
dm_pos=pixel_array(act_dim);
dm_pos=set_pix_arr_data(dm_pos,pos);
dm=set_dm_actuator_positions(dm,dm_pos);
